settings.custom_liquid:
{% if product.metafields.custom.product_highlights %}
  <div class="product-highlights" style="text-align: left; padding: 0 15px; font-size: 15px; font-weight: bold;">
    <ul style="list-style-type: none; padding: 0; margin: 0;">
      {% for highlight in product.metafields.custom.product_highlights.value %}
        {% if highlight contains "." %}
          <li>{{ highlight | replace: ".", "<br> " | raw }}</li>
        {% else %}
          <li>{{ highlight }}</li>
        {% endif %}
      {% endfor %}
    </ul>
  </div>
{% else %}
  <p>No product highlights available.</p>
{% endif %}
